import React from 'react';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const { user } = useAuth() || {}; // üõ°Ô∏è Safe destructure

  return (
    <nav>
      <h1>ShopEZ</h1>
      {user ? <span>Welcome, {user.name}</span> : <span>Guest</span>}
    </nav>
  );
};

export default Navbar;
