import React from 'react';
import { useCart } from '../context/CartContext';

const Cart = () => {
  const { cartItems, removeFromCart, increaseQty, decreaseQty } = useCart();

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Your Cart</h2>

      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          {cartItems.map((item) => (
            <div key={item._id} className="flex items-center justify-between mb-4 border-b pb-2">
              <div>
                <h3 className="font-semibold">{item.name}</h3>
                <p className="text-sm text-gray-500">₹{item.price} x {item.quantity}</p>
              </div>
              <div className="flex items-center gap-2">
                <button onClick={() => decreaseQty(item._id)} className="px-2 py-1 border">-</button>
                <span>{item.quantity}</span>
                <button onClick={() => increaseQty(item._id)} className="px-2 py-1 border">+</button>
                <button onClick={() => removeFromCart(item._id)} className="ml-4 text-red-600">Remove</button>
              </div>
            </div>
          ))}

          <div className="text-right font-bold mt-4">
            Total: ₹{total}
          </div>

          <button className="mt-4 bg-green-600 text-white px-4 py-2 rounded">Proceed to Checkout</button>
        </>
      )}
    </div>
  );
};

export default Cart;
